mapper.insert:
insert into question(
    type, course_id, stem, analysis, difficulty,
    teacher_id, active, deleted, create_time, update_time
)
values (
    #{type}, #{courseId}, #{stem}, #{analysis}, #{difficulty},
    #{teacherId}, #{active}, #{deleted}, #{createTime}, #{updateTime}
)

mapper.select:
select id, type, course_id, stem, analysis, difficulty,
    teacher_id, active, deleted, create_time, update_time
from question

    deleted = 0

        and course_id = #{courseId}


        and difficulty = #{difficulty}


        and stem like concat('%', #{stem}, '%')


        and type = #{type}


order by update_time desc
limit #{pageSize} offset #{offset}

select count(1)
from question

    deleted = 0

        and course_id = #{courseId}


        and difficulty = #{difficulty}


        and stem like concat('%', #{stem}, '%')


        and type = #{type}

mapper.update:
update question
type = #{type}, course_id = #{courseId}, stem = #{stem}, analysis = #{analysis}, difficulty = #{difficulty}, teacher_id = #{teacherId}, active = #{active}, deleted = #{deleted}, update_time = #{updateTime},
where id = #{id}
