mapper.insert:
insert into question(
    type, course_id, stem, analysis, difficulty,
    teacher_id, active, create_time, update_time
)
values (
    #{type}, #{courseId}, #{stem}, #{analysis}, #{difficulty},
    #{teacherId}, #{active}, #{createTime}, #{updateTime}
)

mapper.select:
select id, type, course_id, stem, analysis, difficulty,
    teacher_id, active, deleted, create_time, update_time
from question

    deleted = 0

        and course_id = #{courseId}


        and difficulty = #{difficulty}


        and stem like concat('%', #{stem}, '%')


        and type = #{type}


order by update_time desc
limit #{pageSize} offset #{offset}

SELECT q.id AS question_id, q.stem AS question_stem, e.score AS score, COUNT(f.blank_index) AS blankCount FROM evaluation_expert.question q LEFT JOIN evaluation_expert.question_fill_blank f ON q.id = f.question_id LEFT JOIN evaluation_expert.exam_item e ON q.id = e.question_id WHERE q.id IN
#{i}
GROUP BY q.id, q.stem, e.score

select count(1)
from question

    deleted = 0

        and course_id = #{courseId}


        and difficulty = #{difficulty}


        and stem like concat('%', #{stem}, '%')


        and type = #{type}

select id, type, course_id, stem, analysis, difficulty, teacher_id, active, deleted, create_time, update_time from question where deleted = 0 and id in
#{id}
order by update_time desc

mapper.update:
update question
type = #{type}, course_id = #{courseId}, stem = #{stem}, analysis = #{analysis}, difficulty = #{difficulty}, teacher_id = #{teacherId}, active = #{active}, deleted = #{deleted}, update_time = #{updateTime},
where id = #{id}
